mapper.delete:
DELETE
FROM web_dev.approval_authority
WHERE user_id = #{userId}
  AND course_code = #{courseCode};

mapper.select:
SELECT user_id
FROM web_dev.approval_authority
WHERE course_code = #{courseCode};

SELECT user_id
FROM web_dev.approval_authority
WHERE course_code = #{courseCode}
ORDER BY appro_weight DESC;

SELECT *
FROM web_dev.approval_authority
WHERE approval_authority.user_id = #{userId}
  AND approval_authority.course_code = #{courseCode};
